async function getPlaylistData() {
    loadingError.value = false;
    let [err, result] = await getCloudPlaylistHighquality({
        before: lasttime.value, 
        cat: cat.value, 
        limit: isTopList ? 20 : 10
    })

    if (!err && result) {
